	def seed_database_with_ingredients
		ingredients = @ingredients

		if Ingredient.count == 0
			ingredients.each do |ingredient|
				Ingredient.create!({ :name => ingredient})
				end
			end
		end
	end

class RecipeSeeder
	def initialize
		@recipes = ['Lasagna', 'Grandmas Favorite Cookies', 'Moose Tracks']
	end

	def seed_database_with_recipes
		recipes = @recipes
		user = create_new_user

		if Recipe.count == 0
			recipes.each do |recipe|
				new_recipe = Recipe.new({ 
																:title => recipe,
																:description => 
																	'This Recipes is delicious. The Only Thing Better 
																	than this recipes is finding it on an order menu. It was saucy 
																	goodness and had a greate hint of Hickory smoked bacon'
																		})
				new_recipe.user = user
				new_recipe.save!
			end
		end
	end

	def create_new_user
		User.create!({ 
									:email => 'emasl@ea9ljh4.com', 
									:password => 'password', 
									:password_confirmation => 'password'
								})
	end		
